    private void tNombreUFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_tNombreUFocusLost
       String nombre = tNombreU.getText().trim();
       nombre += ".";
       nombre += tUsuario.getText();
       tUsuario.setText(nombre.toLowerCase());
    }//GEN-LAST:event_tNombreUFocusLost

