    private void tApellidoUFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_tApellidoUFocusLost
        String apellido = tApellidoU.getText().trim();
        String usuario = tUsuario.getText().trim();
        usuario += apellido;
        tUsuario.setText(usuario.toLowerCase());
       
    }//GEN-LAST:event_tApellidoUFocusLost

